field.query:
(select e.body, e.language as lang, '1' as type from dg_editor e, amp_activity a where a.description = e.editor_key and a.amp_activity_id=$parent)
UNION
(select e.body, e.language as lang, '2' as type from dg_editor e, amp_activity a where a.objectives = e.editor_key and a.amp_activity_id=$parent);

SELECT acv.category_value as status
     FROM amp_activity  a, amp_category_value acv,amp_activities_categoryvalues aac, amp_category_class acc
     WHERE aac.amp_activity_id = a.amp_activity_id AND aac.amp_categoryvalue_id=acv.id AND acc.keyname='activity_status' and acv.amp_category_class_id=acc.id and a.amp_activity_id=$parent;

(select 'C' as transaction_type_code, 'Commitment' as transaction_type_name, to_char(df.transaction_date, 'YYYY-MM-DD') as transaction_date, df.currency_code as original_transaction_currency, df.donor_name as donor_name, df.terms_assist_name as finance_type, df.financing_instrument_name as aid_type, df.transaction_amount as original_currency_value, '${currency}' as transaction_currency, (CASE WHEN df.currency_code='${currency}' THEN 1.0 ELSE getExchangeWithFixed('${currency}',df.transaction_date, df.fixed_exchange_rate)/getExchangeWithFixed(df.currency_code,df.transaction_date, df.fixed_exchange_rate) END)*df.transaction_amount AS value
from v_donor_funding df where df.amp_activity_id = $parent and df.transaction_type = 0 and df.adjustment_type_name='Actual' AND df.transaction_date >= '${txStartDate} 00:00:00' AND df.transaction_date < '${txEndDate} 00:00:00' )
UNION
(select 'D' as transaction_type_code, 'Disbursement' as transaction_type_name, to_char(df.transaction_date, 'YYYY-MM-DD') as transaction_date, df.currency_code as original_transaction_currency, df.donor_name as donor_name, df.terms_assist_name as finance_type, df.financing_instrument_name as aid_type, df.transaction_amount as original_currency_value, '${currency}' as transaction_currency, (CASE WHEN df.currency_code='${currency}' THEN 1.0 ELSE getExchangeWithFixed('${currency}',df.transaction_date, df.fixed_exchange_rate)/getExchangeWithFixed(df.currency_code,df.transaction_date, df.fixed_exchange_rate) END)*df.transaction_amount AS value
from v_donor_funding df where df.amp_activity_id = $parent and df.transaction_type = 1 and df.adjustment_type_name='Actual' AND df.transaction_date >= '${txStartDate} 00:00:00' AND df.transaction_date < '${txEndDate} 00:00:00' )
;
